import React from 'react'
import $ from 'jquery'
import './js/countrySelect.js'
import './css/countrySelect.css'


class CountryDropdown extends React.Component {
    constructor(props){
        super(props)
    }
    componentDidMount(){
        this.$el = $(this.el)
        this.$el.countrySelect({
            preferredCountries: this.props.preferredCountries
        });
    }
    render() {
        return (
            <input className={this.props.className} value={this.props.value} ref={el => this.el = el} onChange={this.props.handleChange}/>
         )
    }
}

export default CountryDropdown
