import React, { useEffect, useRef } from 'react'
import './js/countrySelect.js'
import './css/countrySelect.css'


const CountryDropdown = (props) => {
    let inputRef =  useRef(null);

    useEffect(() => {
        inputRef.current.countrySelect({
            preferredCountries: props.preferredCountries
        });
      }, []);


        return (
            <input id={props.id} className={props.className} value={props.value} ref={inputRef} onChange={props.handleChange}/>
         )

}

export default CountryDropdown
