import './App.css';
import CountryDropdown from './components/dropdown-comp';
import React, { useEffect, useState } from 'react';

function App() {
  const [currentInput,setInput] = useState(null);
  useEffect(() => {
    console.log(document.querySelector('#cp').value);
    setInput(document.querySelector('#cp').value);
  }, []);
  return (
    	<CountryDropdown id="cp" className='countryDropdown' preferredCountries={['gb', 'us']} value={currentInput} handleChange={e => console.log(e.target.value)}></CountryDropdown>
  );
}

export default App;
