import './App.css';
import CountryDropdown from './components/dropdown-comp';
import React, { useEffect } from 'react';

function App() {
  useEffect(() => {
    inputRef.current.addEventListener("input", handleChange);
  }, []);
  return (
    	<CountryDropdown className='countryDropdown' showflags={true} preferredCountries={['gb', 'us']} value="" handleChange={e => console.log(e.target.value)}></CountryDropdown>
  );
}

export default App;
