import React from 'react'
import $ from 'jquery'
import './js/countrySelect.js'
import './css/countrySelect.css'

export const SearchBox = ({placeholder,handleChange}) => (
    <FormControl type="search"  placeholder={placeholder} className="mr-8" onChange={handleChange} />
)

class CountryDropdown extends React.Component {
    componentDidMount(){
        this.$el = $(this.el)
        this.$el.countrySelect({
            preferredCountries: ['ca', 'gb', 'us']
        });
    }

    render() {
        return (
                <input  onChange={this.props.handleChange} ref={el => this.el = el}/>
         )
    }
}

export default CountryDropdown
