import './App.css';
import CountryDropdown from './components/dropdown-comp';
import React, { useEffect, useRef } from 'react';


function App() {
  const inputRef = useRef(null);
  const handleChange = (e) => {
    console.log(e.target.value);
  };

  useEffect(() => {
    inputRef.current.el.addEventListener("click", handleChange);
  }, []);
  return (
    	<CountryDropdown ref={inputRef} id="ant" className='countryDropdown' showflags={true} preferredCountries={['gb', 'us']} value="" handleChange={e => console.log(e.target.value)}></CountryDropdown>
  );
}

export default App;
