import React from 'react'
import $ from 'jquery'
import './js/countrySelect.js'
import './css/countrySelect.css'


class CountryDropdown extends React.Component {
    componentDidMount(){
    this.$el = $(this.el)
    this.$el.countrySelect({
        preferredCountries: this.props.preferredCountries
    });
    }
    componentWillReceiveProps(nextProps){
        if(nextProps.value !== this.props.value){
          if(typeof(this.props.onChange) === 'function'){
            this.props.onChange(nextProps.name, nextProps.value)
          }
        }
      }

    render() {
        return (
            <input id={this.props.id} className={this.props.className} value={this.props.value} ref={el => this.el = el} onChange={this.props.handleChange}/>
         )
    }
}

export default CountryDropdown
