import React from 'react'
import $ from 'jquery'
import './js/countrySelect.js'
import './css/countrySelect.css'


class CountryDropdown extends React.Component {
    constructor(props){
        super(props);
        this.state({val:""});
    }
    componentDidMount(){
    this.$el = $(this.el)
    this.$el.countrySelect({
        preferredCountries: this.props.preferredCountries
    });
    $(this.el).change(() => this.setState({val:$(this.el).val()}))

    }

    selectedCountry(){
        console.log(this.state.val)
    }

    render() {
        return (
            <input id={this.props.id} className={this.props.className} onClick={selectedCountry} value={this.props.value} ref={el => this.el = el} onChange={this.props.handleChange}/>
         )
    }
}

export default CountryDropdown
