import React from 'react'
import $ from 'jquery'
import './js/countrySelect.js'
import './css/countrySelect.css'


class CountryDropdown extends React.Component {
     handleChange = (e) => {
        console.log(e.target.value);
    };
    componentDidMount(){
    this.$el = $(this.el);

    let el = React.createRef();
    el = this.el;
    el.addEventListener("input", this.handleChange);
    this.$el.countrySelect({
        preferredCountries: this.props.preferredCountries
    });
    $(this.el).change(() => {
        setTimeout(() => $(this.el).val('change'), 3000);

    }
    );
    }
    render() {
        return (
            <input id={this.props.id} className={this.props.className} onClick={this.selectedCountry} value={this.props.value} ref={el => this.el = el} onChange={this.props.handleChange}/>
         )
    }
}

export default CountryDropdown
