import React, { useRef } from 'react'
import $ from 'jquery'
import './js/countrySelect.js'
import './css/countrySelect.css'


const CountryDropdown = () => {
    useEffect(() => {
        let inputRef =  useRef(null);
        inputRef.countrySelect({
            preferredCountries: this.props.preferredCountries
        });
        inputRef.current.addEventListener("input", handleChange);
      }, []);


        return (
            <input id={this.props.id} className={this.props.className} onClick={this.selectedCountry} value={this.props.value} ref={inputRef} onChange={this.props.handleChange}/>
         )

}

export default CountryDropdown
